import os
module.playlist = []
module.played = ["Silence"]
cache = None
def get_media():
    global cache
    if cache:
        return cache
    else:
        x = []
        for i in os.walk(kaithem.registry.get("jookbawkse/mediadir")):
            for j in i[2]:
                x.append((os.path.basename(j),os.path.join(i[0],j)))
        cache=x
        return x
            
module.get_media = get_media

module.volume = kaithem.widget.Slider(min=0,max=1.2,step=0.01);

def f(usr,val):
    kaithem.sound.setvol(val,"jookbawkse")
module.volume.attach(f)
